import { Row } from 'reactstrap';
import styled from 'styled-components';
import { characteristicsArray } from '../../../utils/constants/Constants';

import * as Styled from './Platform.style'

function Platform() {
    
    return (
        <Styled.Container>
            <Row>
                <Styled.Title>
                    <h1>PLATAFORMA</h1>
                    <p>El servidor en la nube ofrece el monitoreo remoto desde cualquier dispositivo inteligente, brindándole supervisión en tiempo real de sus productos y procesos.</p>
                </Styled.Title>
            </Row>
            {characteristicsArray.map((item) => {
                return (
                        <Styled.Card>
                            <div className="image-div">
                                <img alt='image' src={item.image}/>
                            </div>
                            <div className="text-div">
                                <h5>{item.text}</h5>
                            </div>
                        </Styled.Card>
                )
            })}
        </Styled.Container>
    )
}

export default Platform
