import styled from "styled-components";

export const Container = styled.div`
    display: grid;
    grid-template-columns: 30% 70%;
    background-color: #F2F2F2;
    padding-left: 5rem;
    padding-right: 5rem;
    padding-top: 5rem;
    padding-bottom: 5rem;
    font-family: 'Montserrat', sans-serif;

    ${(props) => props.theme.breakpoints.down('md')} {
        display: block;
        padding-left: 2rem;
        padding-right: 2rem;
    }
    
    @keyframes cube {
        from {
            transform: scale(0) rotate(0deg) translate(-50%, -50%);
            opacity: 1;
        }
        to {
            transform: scale(20) rotate(960deg) translate(-50%, -50%);
            opacity: 0;
        }
    }
    
    .background {
        position: fixed;
        width: 100vw;
        height: 100vh;
        top: 0;
        left: 0;
        margin: 0;
        padding: 0;
        background: #ffffff;
        overflow: hidden;
    }
    .background li {
        position: absolute;
        top: 80vh;
        left: 45vw;
        width: 10px;
        height: 10px;
        border: solid 1px #e5e5e5;
        color: transparent;
        transform-origin: top left;
        transform: scale(0) rotate(0deg) translate(-50%, -50%);
        animation: cube 7s ease-in forwards infinite;
    }
    undefined
    .background li:nth-child(0) {
        animation-delay: 0s;
        left: 37vw;
        top: 68vh;
        border-color: #ffffff;
    }
    
    .background li:nth-child(1) {
        animation-delay: 2s;
        left: 27vw;
        top: 11vh;
    }
    
    .background li:nth-child(2) {
        animation-delay: 4s;
        left: 8vw;
        top: 57vh;
    }
    
    .background li:nth-child(3) {
        animation-delay: 6s;
        left: 57vw;
        top: 85vh;
        border-color: #ffffff;
    }
    
    .background li:nth-child(4) {
        animation-delay: 8s;
        left: 71vw;
        top: 28vh;
    }
    
    .background li:nth-child(5) {
        animation-delay: 10s;
        left: 95vw;
        top: 38vh;
    }
    
`

export const Info = styled.div`
    h1 {
        color: #FF5B2B;
        text-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
        text-transform: uppercase;
        font-family: 'Alatsi', sans-serif;
    }

    p {
        text-align: justify;
    }
`

export const Boxes = styled.div`
    display: flex;
    justify-content: space-evenly;
    flex-wrap: wrap;
`

export const Box = styled.div`
    border: 3px solid #FF5B2B;
    box-sizing: border-box;
    width: 15rem;
    text-align: center;
    padding: 10px;
    margin-bottom: 1rem;
    transition: border 0.5s, box-shadow 1s;
    
    &:hover {
        border: 5px solid #FFB300;
        box-shadow: 5px 10px 16px #d2d2d2;
    }

    img {
        width: 6rem;
    }

    .platform, .analisis {
        width: 5rem;
        margin: 0.8rem;
    }

    h4 {
        color: #FF5B2B;
        margin-bottom: 1rem;
        font-weight: bold;
        font-family: 'Alatsi', sans-serif;
    }
`
