import styled from "styled-components";

export const Container = styled.div`
    padding-left: 5rem;
    padding-right: 5rem;
    padding-top: 5rem;
    padding-bottom: 5rem;
    background-color: #E14F25;
    text-align: center;
    font-family: 'Montserrat', sans-serif;

    ${(props) => props.theme.breakpoints.down('md')} {
        padding-left: 2rem;
        padding-right: 2rem;
    }
`

export const Title = styled.div`
    text-align: center;

    h1 {
        color: white;
        text-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
        margin-bottom: 3rem;
        font-family: 'Alatsi', sans-serif;
    }
`

export const CardsContainer = styled.div``

export const Card = styled.div`
    box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
    border-radius: 50px;
    margin-bottom: 3rem;
    padding: 2rem;
    display: grid;
    grid-template-columns: 40% 60%;

    background: linear-gradient(145deg, #e65227, #ff612e);
    box-shadow:  7px 7px 14px #bd4320,
                 -7px -7px 14px #ff7336;

    ${(props) => props.theme.breakpoints.down('md')} {
        display: block;
    }

    
    .image-div {

            img {
                width: 5rem;
                margin-right: 5rem;
                float: right;

                ${(props) => props.theme.breakpoints.down('md')} {
                    margin-bottom: 1rem;
                    float: inline-end;
                 }
            }

    }

    .text-div {
        display: flex;
        align-items: center;
        float: left;

        ${(props) => props.theme.breakpoints.down('md')} {
            justify-content: center;
            float: inline-end;
         }
    }

`
