import React, { useEffect } from 'react';
import { useInView } from 'react-intersection-observer';
import { Row } from 'reactstrap';
import styled from 'styled-components';
import { characteristicsArray } from '../../../utils/constants/Constants';
import TextCarousel from '../../global/homeHeader/HomeHeader';

import * as Styled from './Characteristics.style'

function Characteristics() {
    const [className, setClassName] = React.useState(false)

    const activateAnimation = () => {
        if (window.scrollY >= 3312 && window.scrollY >= 3312) {
            setClassName(true)
        } else {
            setClassName(false)
        }
      }    
    
    window.addEventListener('scroll', activateAnimation);

    console.log(window.scrollY)

    return (
        <Styled.Container>
            <Styled.Layer>
            <Row>
                <Styled.Title>
                    <h1>CARACTERÍSTICAS DE LA SOLUCIÓN DE iOTEK</h1>
                </Styled.Title>
            </Row>
            <Styled.CardsContainer>
                {characteristicsArray.map((item) => {
                    return (
                        <Styled.Card className={className ? 'animation' : ''}>
                            <div className="image-div">
                                <img alt='image' src={item.image}/>
                            </div>
                            <div className="text-div">
                                <h5>{item.text}</h5>
                            </div>
                        </Styled.Card>
                    )
                })}
                </Styled.CardsContainer>
            </Styled.Layer>
        </Styled.Container>
    )
}

export default Characteristics
