import styled from "styled-components";

export const Container = styled.div`
    padding-left: 5rem;
    padding-right: 5rem;
    padding-top: 5rem;
    padding-bottom: 5rem;
`

export const Title = styled.div`
    text-align: center;

    h1 {
        color: #FF5B2B;
        text-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
        text-transform: uppercase;
        margin-bottom: 1rem;
    }
`

export const SolutionTitle = styled.div`
    margin-left: 5rem;
    margin-top: 3rem;
    margin-bottom: 2rem;
    width: auto;
    border-bottom: 2px solid;
    color: #FF5B2B;
`

export const Boxes = styled.div`
    display: flex;
    justify-content: space-evenly;
    flex-wrap: wrap;
`

export const Box = styled.div`
    box-sizing: border-box;
    width: 18rem;
    text-align: center;
    padding: 2rem;;
    margin-bottom: 1rem;
    background: #F5F5F5;
    box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
    border-radius: 8px;

    img {
        width: 4rem;
        margin-bottom: 1rem;
        margin-top: 1rem;
    }

    h4 {
        color: #FF5B2B;
        margin-bottom: 1rem;
    }
`
