import React from 'react';
import { HomeCarouselText } from '../../../utils/constants/Constants';
import AwesomeSlider from 'react-awesome-slider';
import AwesomeSliderStyles from 'react-awesome-slider/src/styled/scale-out-animation/index';
import LetteringStyles from 'react-awesome-slider/src/hoc/animated-lettering/index'
import withAutoplay from 'react-awesome-slider/dist/autoplay';
import 'react-awesome-slider/dist/styles.css';
import 'react-awesome-slider/dist/captioned.css';
 
import * as Styled from './TextCarousel.style'

function TextCarousel() {
  const AutoplaySlider = withAutoplay(AwesomeSlider);

  return (
    <AutoplaySlider
      play={true}
      cancelOnInteraction={false} // should stop playing on user interaction
      interval={6000}
      cssModule={[AwesomeSliderStyles, LetteringStyles]}
      mobileTouch
      bullets

  >
      {HomeCarouselText.map((item, i) => {
        return (
          <div key={i} className=''>

          </div>
        )
      })}
  </AutoplaySlider>
    // <Styled.Container>
     
    //   <Styled.MoveSlider >
		// 	  <i id="arrow-down-slider" className="fa fa-chevron-down arrow-down-slider" aria-hidden="true"/>
    //   </Styled.MoveSlider>

    // </Styled.Container>
    )
}

export default TextCarousel

