import { Row } from 'reactstrap';
import { SolutionsArray } from '../../../utils/constants/Constants';
import { useInView } from "react-intersection-observer";

import * as Styled from './Solutions.style'
import React, { useEffect } from 'react';
import { Button, createStyles, makeStyles, Theme } from '@material-ui/core';

function Solutions() {
    const [ref, inView] = useInView()
    const [className, setclassName] = React.useState('')
    const [openCard, setOpenCard] = React.useState('hide')
    const [buttonMessage, setButtonMessage] = React.useState('Leer más')
    const [initialState, setInitialState] = React.useState(false)

    useEffect(() => {
        if (inView) {
            console.log('entre en solutions')
            setclassName('animation')
        }
    }, [inView]);

    const handleButton = () => {
        if (initialState) {
            setOpenCard('open')
            setButtonMessage('Leer menos')
        }
        else {
            setOpenCard('hide')
            setButtonMessage('Leer más')
        }

        setInitialState(!initialState)

    }
    
    const styleClass = makeStyles((theme: Theme) =>
        createStyles({
            root: {
            '& > *': {
                margin: theme.spacing(1),
            },
            },
            containedPrimary: {
                backgroundColor: '#ca2929',
                '&:hover': {
                    backgroundColor: '#ac0f0f',
                }
            },
        }),
    );

    return (
        <Styled.Container>
            <Row>
                <Styled.Title>
                    <h1>Nuestras Soluciones</h1>
                    <p>Nuestros sensores y dispositivos de almacenamiento de datos en la nube aportan eficiencia y seguridad a sus procesos.</p>
                </Styled.Title>
            </Row>
            {SolutionsArray.map((item, i) => {
                return (
                    <Row>
                        <Styled.SolutionTitle ref={ref} >
                            <h2>{item.title}</h2>
                        <Button className={styleClass().containedPrimary} variant="contained" color="primary" onClick={() => handleButton()}>{buttonMessage}</Button>
                        </Styled.SolutionTitle>
                        <Styled.Boxes ref={ref} >
                            {item.solutions?.map((solution) => {
                                return (
                                    <Styled.Card>
                                        <Styled.Face className='face1'>
                                            <Styled.Content>
                                                <span className="stars"></span>
                                                <p>{solution.caption}</p>
                                            </Styled.Content>
                                            <Styled.Face className='face2'>
                                                <img alt={solution.title} src={solution.image} />
                                                <h2>{solution.title}</h2>
                                            </Styled.Face>
                                        </Styled.Face>
                                    </Styled.Card>


                                    // <Styled.Box className={className} ref={ref}>
                                    //     <img alt={solution.title} src={solution.image} />
                                    //     <h4>{solution.title}</h4>
                                    //     <p className={openCard}>{solution.caption}</p>
                                    // </Styled.Box>
                                )
                            })}
                        </Styled.Boxes>
                    </Row>
                )
            })}
        </Styled.Container>
    )
}

export default Solutions
