import { Row } from 'reactstrap';
import { SolutionsArray } from '../../../utils/constants/Constants';
import { useInView } from "react-intersection-observer";

import * as Styled from './Solutions.style'
import React, { useEffect } from 'react';
import { Button, createStyles, makeStyles, Theme } from '@material-ui/core';

function Solutions() {
    const [ref, inView] = useInView()
    const [classNameName, setclassNameName] = React.useState('')
    const [openCard, setOpenCard] = React.useState('hide')
    const [buttonMessage, setButtonMessage] = React.useState('Leer m치s')
    const [initialState, setInitialState] = React.useState(false)

    useEffect(() => {
        if (inView) {
            console.log('entre en solutions')
            setclassNameName('animation')
        }
    }, [inView]);

    const handleButton = () => {
        if (initialState) {
            setOpenCard('open')
            setButtonMessage('Leer menos')
        }
        else {
            setOpenCard('hide')
            setButtonMessage('Leer m치s')
        }

        setInitialState(!initialState)

    }
    
    const classNamees = makeStyles((theme: Theme) =>
        createStyles({
            root: {
            '& > *': {
                margin: theme.spacing(1),
            },
            },
            containedPrimary: {
                backgroundColor: '#ca2929',
                '&:hover': {
                    backgroundColor: '#ac0f0f',
                }
            },
        }),
    );

    return (
        <Styled.Container>
            <Row>
                <Styled.Title>
                    <h1>Nuestras Soluciones</h1>
                    <p>Nuestros sensores y dispositivos de almacenamiento de datos en la nube aportan eficiencia y seguridad a sus procesos.</p>
                </Styled.Title>
            </Row>
            {SolutionsArray.map((item, i) => {
                return (
                    <Row>
                        <Styled.SolutionTitle ref={ref} >
                            <h2>{item.title}</h2>
                        <Button className={classNamees().containedPrimary} variant="contained" color="primary" onClick={() => handleButton()}>{buttonMessage}</Button>
                        </Styled.SolutionTitle>
                        <Styled.Boxes ref={ref} >
                            {item.solutions?.map((solution) => {
                                return (
                                    <Styled.Box className={classNameName} ref={ref}>
                                        <img alt={solution.title} src={solution.image} />
                                        <h4>{solution.title}</h4>
                                        <p className={openCard}>{solution.caption}</p>
                                    </Styled.Box>
                                )
                            })}
    <div className="card">
    <div className="face face1">
      <div className="content">
        <span className="stars"></span>
        <h2 className="java">Java</h2>
        <p className="java">Java is a class-based, object-oriented programming language that is designed to have as few implementation dependencies as possible.</p>
      </div>
    </div>
    <div className="face face2">
      <h2>01</h2>
    </div>
  </div>
                        </Styled.Boxes>
                    </Row>
                )
            })}
        </Styled.Container>
    )
}

export default Solutions
