import React, { useEffect } from 'react';
import { useInView } from 'react-intersection-observer';
import { Row } from 'reactstrap';
import styled from 'styled-components';
import { characteristicsArray } from '../../../utils/constants/Constants';
import TextCarousel from '../../global/carousel/TextCarousel';

import * as Styled from './Characteristics.style'

function Characteristics() {
    const [ref, inView] = useInView()
    const [className, setClassName] = React.useState('')

    
    useEffect(() => {
        if (inView) {
            console.log('entre en characteristics')
            setClassName('animation')
        }
      }, [inView]);

    return (
        <Styled.Container>
            <Row>
                <Styled.Title>
                    <h1>CARACTERÍSTICAS DE LA SOLUCIÓN DE iOTEK</h1>
                </Styled.Title>
            </Row>
            <Styled.CardsContainer>
                {characteristicsArray.map((item) => {
                    return (
                        <Styled.Card className='on-scroll' onScroll={handleScroll}>
                            <div className="image-div">
                                <img alt='image' src={item.image}/>
                            </div>
                            <div className="text-div">
                                <h5>{item.text}</h5>
                            </div>
                        </Styled.Card>
                    )
                })}
            </Styled.CardsContainer>

        </Styled.Container>
    )
}

export default Characteristics
