import styled from "styled-components";

export const Container = styled.div`
    padding-left: 5rem;
    padding-right: 5rem;
    padding-top: 5rem;
    padding-bottom: 5rem;
    text-align: center;
`

export const Title = styled.div`
    text-align: start;
    margin-bottom: 3rem;

    h1 {
        color: #FF5B2B;
        text-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
    }
`

export const Card = styled.div`
    background: #FFFFFF;
    box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
    border-radius: 8px;
    margin-bottom: 2rem;
    padding: 2rem;
    
    .header-div {
        
            img {
                width: 5rem;
                margin-right: 5rem;
            }

    }

    .text-div {
       
    }

`
